mapper.insert:
insert into
Question_Time (
questionTimeNum
,itemNum
,questionTitle
,description
,startDate
,dueDate
,memberId
)
VALUES (
question_Time_seq.NEXTVAL
,#{itemNum}
,#{questionTitle}
,#{description}
,#{startDate}
,#{dueDate}
,#{memberId}
)

insert into
Question (
questionNum
,questionTimeNum
,question
)
VALUES (
Question_seq.NEXTVAL
,#{questionTimeNum}
,#{question}
)

mapper.update:
UPDATE Question_Time

SET
questionTitle = #{questionTitle}
,description = #{description}
,startDate = #{startDate}
,dueDate = #{dueDate}
WHERE questionTimeNum=#{questionTimeNum}

UPDATE Question

SET
question = #{question}

WHERE questionNum=#{questionNum}


UPDATE Question_Time
SET
etc = #{etc}
WHERE questionTimeNum=#{questionTimeNum}

UPDATE Question
SET question = 'none'
WHERE questionNum=#{questionNum}

mapper.insert:
insert into
Survey (
surveyNum
,questionNum
,qValuable
,writtenDate
)
VALUES (
Survey_seq.NEXTVAL
,#{questionNum}
,#{qValuable}
,SYSDATE
)

mapper.update:
UPDATE Question_Time
SET questionTitle = 'none'
WHERE questionTimeNum=#{questionTimeNum}

mapper.select:
	select i.itemtype, q.questiontitle, q.memberid, q.startdate, q.questionTimeNum, q.dueDate,i.itemnum
from Question_Time q inner join item i on i.itemnum=q.itemnum


where q.memberId=#{memberId}

select i.itemnum, i.saveitemimage , i.itemImagename
from Item i inner join Question_Time qt on i.itemNum = qt.itemNum
where qt.questionTimeNum = #{questionTimeNum}
