mapper.select:
select 
*
from devMember
where memberId=#{memberId} 
and memberPw=#{memberPw}

select  
memberId
,memberPw
from devMember
where memberName=#{memberName}
and email=#{email}

and memberId=#{memberId}

mapper.update:
UPDATE devMember 
SET 
memberPw = #{memberPw}
,email = #{email}
,phoneNum=#{phoneNum}
where 
memberId=#{memberId}

UPDATE devMember 
SET 
memberType = 'none'
where 
memberId=#{memberId}
and memberPw=#{memberPw}

update 
	devMember
set 
	fundPrice = #{fundPrice} 
where
	memberId = #{memberId}
