mapper.insert:
insert into FundingOption (
	fundingOptionNum
	,crowdfundingNum
	,optionType
	,optionPrice
	)
	VALUES (
	fundingOption_seq.NEXTVAL
	,#{crowdfundingNum}
	,#{optionType}
	,#{optionPrice}
	)

mapper.update:
UPDATE FundingOption 
SET 
optionType = #{optionType}
,optionPrice = #{optionPrice}
where 
fundingOptionNum=#{fundingOptionNum}
