mapper.insert:
insert into
Item_Question (
questionNum
,itemNum
,question
,description
,dueDate
,startDate
)
VALUES (
Item_Question_seq.NEXTVAL
,#{itemNum}
,#{question}
,#{description}
,#{dueDate}
,#{startDate}
)

mapper.select:

select *
from Item_Question
where itemNum=#{itemNum}
and SYSDATE between  #{startDate}) AND #{dueDate}

mapper.update:
UPDATE Item_Question

SET
question = #{question}
,description = #{description}
,dueDate = #{dueDate}
,startDate = #{startDate}

WHERE questionNum=#{questionNum}

UPDATE Item_Question
SET
etc = #{etc}
WHERE questionNum=#{questionNum}

UPDATE Item_Question
SET question = 'none'
WHERE questionNum=#{questionNum}

mapper.insert:
insert into
Item_Survey (
surveyNum
,questionNum
,qValuable
,writtenDate
)
VALUES (
Item_Survey_seq.NEXTVAL
,#{questionNum}
,#{qValuable}
,SYSDATE
)

mapper.update:
UPDATE Item_Survey
SET
qValuable = #{qValuable}
WHERE surveyNum=#{surveyNum}
