mapper.update:
update BOARD set
HITCOUNT = HITCOUNT + 1
where boardnum = #{boardNum}

mapper.insert:
INSERT INTO BOARD
(	
	boardNum,
	id,
	TITLE,
	CONTENT,
	QTYPE,
	QCATEGORY,

	originalFilename,
	saveFilename,

	boardDate

)
VALUES
(
BOARD_SEQ.nextval
	, #{id}
	, #{title}
	, #{content}
	, #{qType}
	, #{qCategory}

	, #{originalFilename}
	, #{saveFilename}

	, #{boardDate}
)
